import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';



const root = ReactDOM.createRoot(document.getElementById('root'));
let name = "Богдан";
let surname = "Чудопалов"
const items = ['Элемент 1', 'Элемент 2', 'Элемент 3', 'Элемент 4', 'Элемент 5', 'Элемент 6'];
let now = new Date();
function getRandom() {
  return Math.floor(Math.random() * 10);
}
root.render(
  <div>
    <h1 className="h-style">Какое-то значение</h1>
    <h2 className='h-style'>{name} {surname}</h2>
    <ul>
      {
        Array.from({ length: 5 }, (_, index) =>
          <li key={index}>{`Элемент ${getRandom()} ${getRandom() % 2 === 0 ? "EVEN" : "ODD"}`}</li>)
      }
    </ul>
    <div className="container col-10">
      <div className="card">
        <img src="http://via.placeholder.com/200" className="card-img-top" alt="..." />
        <div className="card-body">
          <h5 className="card-title">Card title</h5>
          <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
          <a href="#" className="btn btn-primary">Go somewhere</a>
        </div>
      </div>
    </div>
    <p> &copy; Авторсике права мои {now.getFullYear()}</p>
  </div>
);
