import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';



const root = ReactDOM.createRoot(document.getElementById('root'));
let name = "Богдан";
let surname = "Чудопалов"
const items = ['Элемент 1', 'Элемент 2', 'Элемент 3', 'Элемент 4', 'Элемент 5', 'Элемент 6'];
let now = new Date();
function getRandom() {
  return Math.floor(Math.random() * 10);
}
root.render(
  <div>
    <h1 className="h-style">Какое-то значение</h1>
    <h2 className="h-style">{name} {surname}</h2>
    <ul>
      {
        Array.from({ length: 5 }, (_, index) =>
          <li key={index}>{`Элемент ${getRandom()} ${getRandom() % 2 === 0 ? "EVEN" : "ODD"}`}</li>)
      }
    </ul>
    <p> &copy; Авторсике права мои {now.getFullYear()}</p>
  </div>
);
